import React, { useState } from 'react';
import blogdetailsservice from './blogdetailsservice';
import { useNavigate } from 'react-router-dom';

export default function RegistrationForm() {
  const navigate = useNavigate();
  const [errors, setErrors] = useState({});

  const [formData, setFormData] = useState({
    name: '',
    mobileNumber: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const newErrors = {};
    const { name, mobileNumber, email, password, confirmPassword } = formData;
  
    if (!name.trim()) {
      newErrors.name = "Name is required.";
    }
  
    if (!/^\d{10}$/.test(mobileNumber)) {
      newErrors.mobileNumber = "Enter a valid 10-digit mobile number.";
    }
  
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      newErrors.email = "Enter a valid email address.";
    }
  
    if (password.length < 6) {
      newErrors.password = "Password must be at least 6 characters.";
    }
  
    if (password !== confirmPassword) {
      newErrors.confirmPassword = "Passwords do not match.";
    }
  
    setErrors(newErrors);
  
    if (Object.keys(newErrors).length === 0) {
      blogdetailsservice.saveUserDetails(formData)
        .then(() => {
          alert("User registered successfully.\nNow you will be redirected to login page");
          navigate("/tologin");
        })
        .catch(err => {
          alert("Registration failed: " + err.message);
        });
    }
  };
  
  

  

  return (
    <form onSubmit={handleSubmit} className="form-container">
      
      <div>
        <h2>Register an account</h2>
        <label>Name : </label>
        <input
          id="name"
          placeholder='Enter your full name'
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
        />
      </div>

      <div>
        <label>Mobile Number : </label>
        <input
          
          id="mobileNumber"
          placeholder="Enter 10-digit number"
          type="tel"
          name="mobileNumber"
          value={formData.mobileNumber}
          onChange={handleChange}
        />
      </div>
      
      <div>
        <label>Email:</label>
        <input
          id='email'
          placeholder='Enter your email address'
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Password : </label>
        <input
          id='password'
          placeholder='Enter your password'
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Confirm Password : </label>
        <input
          id='confirmPassword'
          placeholder='Confirm your password'
          type="password"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleChange}
        />
      </div>


      <button type="submit">
        Register
      </button>
    </form>
  );
}
