import axios from 'axios';

const URL = 'http://localhost:9300/api'; 

const saveUserDetails = (userData) => {
  return axios.post(`${URL}/register`, userData);
};

const validateUser = (mobileNumber, password) => {
  return axios.post(`${URL}/login`, {
    mobileNumber,
    password
  });
};

export default {
  saveUserDetails,
  validateUser
};
