import React, { useState } from 'react';
import blogdetailsservice from './blogdetailsservice';
import { useNavigate } from 'react-router-dom';
import './styles.css';

export default function RegistrationForm() {
  const [formData, setFormData] = useState({
    name: '',
    mobileNumber: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();
  

  const validate = () => {
    const newErrors = {};

    if (!formData.name.trim()) newErrors.name = 'Name is required.';
    if (!/^\d{10}$/.test(formData.mobileNumber)) newErrors.mobileNumber = 'Enter a valid 10-digit number.';
    if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Enter a valid email.';
    if (formData.password.length < 6) newErrors.password = 'Password must be at least 6 characters.';
    if (formData.password !== formData.confirmPassword) 
        {
        newErrors.confirmPassword = "Password do not match";
        }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;

    blogdetailsservice.saveUserDetails(formData)
      .then(() => {
        alert("User registered successfully.\nNow you will be redirected to login page");
        navigate("/tologin");
      })
      .catch(err => {
        alert("Registration failed: " + err.message);
      });
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      <h2>Register an account</h2>

      <div>
        <label>Name:</label>
        <input
          id="name"
          name="name"
          type="text"
          placeholder="Enter your full name"
          value={formData.name}
          onChange={handleChange}
        />
        {errors.name && <p className="error">{errors.name}</p>}
      </div>

      <div>
        <label>Mobile Number:</label>
        <input
          id="mobileNumber"
          name="mobileNumber"
          type="tel"
          placeholder="Enter 10-digit number"
          value={formData.mobileNumber}
          onChange={handleChange}
        />
        {errors.mobileNumber && <p className="error">{errors.mobileNumber}</p>}
      </div>

      <div>
        <label>Email:</label>
        <input
          id="email"
          name="email"
          type="email"
          placeholder="Enter your email address"
          value={formData.email}
          onChange={handleChange}
        />
        {errors.email && <p className="error">{errors.email}</p>}
      </div>

      <div>
        <label>Password:</label>
        <input
          id="password"
          name="password"
          type="password"
          placeholder="Enter your password"
          value={formData.password}
          onChange={handleChange}
        />
        {errors.password && <p className="error">{errors.password}</p>}
      </div>

      <div>
        <label>Confirm Password:</label>
        <input
          id="confirmPassword"
          name="confirmPassword"
          type="password"
          placeholder="Confirm your password"
          value={formData.confirmPassword}
          onChange={handleChange}
        />
        {errors.confirmPassword && <p className="error">{errors.confirmPassword}</p>}
      </div>

      <button type="submit">Register</button>
    </form>
  );
}
